import styled from "styled-components";

import CustomButton from "../custom-button/custom-button";

export const CollectionItemStyle = styled.div`
  width: 22vw;
  display: flex;
  flex-direction: column;
  height: 350px;
  align-items: center;
  position: relative;
`;

export const ImageStyle = styled.div`
  width: 100%;
  height: 95%;
  background-size: cover;
  background-position: center;
  margin-bottom: 5px;
`;

export const ColectionFooterStyle = styled.div`
  width: 100%;
  height: 5%;
  display: flex;
  justify-content: space-between;
  font-size: 18px;
`;

export const NameSpanStyle = styled.span`
  width: 90%;
  margin-bottom: 15px;
`;

export const PriceSpanStyle = styled.span`
  width: 10%;
`;

export const CustomButtonStyle = styled(CustomButton)`
width: 80%;
opacity: 0.7;
position: absolute;
top: 255px;
display: none;
}


`;
